mapper.delete:

delete 
 from agent
 where no in ${delUsrs}
 and license_no = #{licenseNo}

mapper.update:

update license 
 set current_count = current_count-1
 where no = ${_parameter};

mapper.select:

select * 
  from general_user gu, agent ag
  where gu.email = #{email}
  and ag.license_no = #{licenseNo}
  and ag.user_no = gu.no
  order by gu.no ASC

mapper.update:

update general_user
  set password = #{password},
  name = #{name},
  dept_name = #{dept}
 where email = #{email}


update license 
 set current_count = current_count+1
 where no = ${_parameter};

mapper.delete:

delete  
 from agent 
 where no in ${_parameter}

mapper.select:

select * 
  from agent
  where license_no = #{licenseNo}
  and mac_address = #{macaddr}
  order by no asc


select * 
  from agent
  where license_no = #{licenseNo}
  and ip_address = #{ipaddr}
  order by ag.no asc

mapper.update:

update agent 
 set activation = not activation
 where no in ${activeUsrs}
 and license_no = #{licenseNo}


update license
 set end_date = to_timestamp(#{enddate}, 'YYYY-MM-DD HH24:MI:SS')
 where no = ${licenseNo}
