mapper.delete:

delete 
 from general_user
 where no in ${_parameter}


delete 
 from agent
 where user_no in ${_parameter}

mapper.update:

update license 
 set current_count = current_count-1
 where no = ${_parameter};

mapper.select:

select * 
  from general_user gu, agent ag
  where gu.email = #{email}
  and ag.license_no = #{licenseNo}
  and ag.user_no = gu.no

mapper.update:

update license 
 set current_count = current_count+1
 where no = ${_parameter};

mapper.delete:

delete 
 from agent
 where no in ${_parameter}

mapper.select:

select * 
  from agent
  where license_no = #{licenseNo}
  and mac_address = #{macaddr}


select * 
  from agent
  where license_no = #{licenseNo}
  and ip_address = #{ipaddr}
