mapper.select:

select * from subscription_policy
where visible=true 
order by no;

mapper.update:

update subscription_policy 
  set policy_title = #{policy_title} , visible = #{visible} 
 where no = #{no};


update subscription_policy 
  set policy_title = #{policy_title} , visible = #{visible} ,
  standard = #{standard}, max_count = #{max_count}, 
  price = #{price}, url = #{url}
 where no = #{no};
